import React, {useState, useEffect} from 'react';
import './App.css';

function Shop() {

  useEffect(() => {
    fetchItems()
  }, []);

  const [items, setItems] = useState([]);

  const fetchItems = async () => {
    const data = await fetch(
      'https://www.dnd5eapi.co/api/monsters/adult-black-dragon/'
    );

    const items = await data.json();
    console.log(items);
    setItems(items);
  };

  return (
      <div>
          <h1>Shop page</h1>
          {items.map(item => (
            <h2>{item.name}</h2>
          ))}
      </div>
  );
}

export default Shop;
